void PPort_SCr2EBS::Start()
{
    m_logger.LogVerbose() << "PPort_SCr2EBS::Start";
    
    // construct skeleton
    ara::core::InstanceSpecifier specifier{"Exe_ServiceCreator/AA/PPort_SCr2EBS"};
    m_interface = std::make_unique<oss::srv::SCr2EBS::skeleton::EBS_SI_ServiceCreatorSkeletonImpl>(specifier);
    
    // offer service
    auto offer = m_interface->OfferService();
    if (offer.HasValue())
    {
        m_running = true;
        m_logger.LogVerbose() << "PPort_SCr2EBS::Start::OfferService";
    }
    else
    {
        m_running = false;
        m_logger.LogError() << "PPort_SCr2EBS::Start::OfferService::" << offer.Error().Message();
    }
}
 
void PPort_SCr2EBS::Terminate()
{
    m_logger.LogVerbose() << "PPort_SCr2EBS::Terminate";
    
    // stop port
    m_running = false;
    
    // stop offer service
    m_interface->StopOfferService();
    m_logger.LogVerbose() << "PPort_SCr2EBS::Terminate::StopOfferService";
}
 
